'SELECT project_id,',
'       version,',
'       apex_escape.html_whitelist(project_name,q''!''!'') project_name,',
'       application_id,',
'       apex_escape.html(domain) domain,',
'       last_updated_date,',
'       last_updated_by,',
'       theme_number,',
'       decode(write_access,''Yes'',project_id,null) write_access_p_id,',
'       decode(write_access,''Yes'',''normal'',''none'') edit_display,',
'       access_restrictions,',
'       ataf_pkg.CURRENT_USER_IN_GROUP(''ataf_administrator'') admin,',
'       write_access,',
'       decode(owner,:app_user,''Yes'',''No'') IS_OWNER',
'      ',
'FROM',
'(',
'SELECT DISTINCT',
'   ap.owner,',
'   ap.project_id,',
'   ap.version, ',
'   ap.project_name, ',
'   ap.application_id, ',
'   APEX_UTIL.URL_ENCODE(ap.domain) DOMAIN, ',
'   ap.last_updated_date, ',
'   ap.last_updated_by,',
'   aat.theme_number,',
'   ap.access_restrictions,',
'   nvl2(ur.project_id,''Yes'',''No'') user_restrictions,',
'   case',
'     when ur.access_level = ''Write'' then ''Yes''',
'     -- when ap.access_restrictions IS NULL THEN ''Yes''',
'     when ap.access_restrictions IS NULL AND ap.owner = :APP_USER THEN ''Yes''',
'     when ataf_pkg.CURRENT_USER_IN_GROUP(''ataf_administrator'') = ''Y'' THEN ''Yes''',
'     when ap.owner = :app_user then ''Yes''',
'   else ''No''',
'   end write_access',
'FROM ',
'   ataf_project ap',
'JOIN apex_applications aat ON ap.application_id = aat.application_id',
'LEFT OUTER JOIN ataf_user_restrictions ur ON ap.project_id = ur.project_id',
'     AND ur.username = :app_user',
')',
'WHERE ',
'  user_restrictions = ''Yes''  -- User exists in the User Restrictions',
