'  lv_action_id ataf_action.action_id%TYPE;',
'  lv_item_type ataf_action.item_type%TYPE;',
'  lv_action ataf_action.action%TYPE;',
'  lv_script ataf_action.script%TYPE;',
'  lv_data_yn ataf_action.data_yn%TYPE;',
'  lv_notes  ataf_action.notes%TYPE;',
'  lv_and_wait ataf_action.and_wait%TYPE;',
'BEGIN',
'------------------------',
'-- Insert into Action --',
'------------------------',
'   select ',
'     ITEM_TYPE,',
'     ACTION,',
'     SCRIPT,',
'     DATA_YN,',
'     NOTES,',
'     AND_WAIT',
'   into',
'     lv_item_type,',
'     lv_action,',
'     lv_script,',
'     lv_data_yn,',
'     lv_notes,',
'     lv_and_wait',
'   from ATAF_ACTION',
'   WHERE action_id = :p4_action_id;',
'',
'',
'  INSERT INTO ataf_action (',
'     ITEM_TYPE,',
'     ACTION,',
'     PROJECT_ID,',
'     SCRIPT,',
'     DATA_YN,',
'     NOTES,',
'     AND_WAIT)',
'   VALUES  (',
'     lv_item_type,',
'     lv_action||'' - Copy'',',
'     :PROJECT_ID,',
'     lv_script,',
'     lv_data_yn,',
'     lv_notes,',
'     lv_and_wait)',
'   RETURNING action_id INTO lv_action_id;',
'------------------------',
'-- Insert into Action --',
'------------------------',
